<?include_once('models/startup.php');include_once('models/article.php');// Установка параметров, подключение к БД, запуск сессии.startup();if (isset($_GET['id'])) {        $article = articles_get($_GET['id']);    $ar_title = $article['title'];    $ar_content = $article['content'];    $id = $_GET['id'];    }if (isset($_POST['id'])) {        $success = articles_edit($_POST['id'], $_POST['title'], $_POST['content']);    $id = $_POST['id'];    $article = articles_get($_POST['id']);    $ar_title = $article['title'];    $ar_content = $article['content'];}if (isset ($_POST['delete'])) {        articles_delete($_POST['id']);    }// Кодировка.header('Content-type: text/html; charset=windows-1251');// Вывод в шаблон.//include('theme/default/v_edit.php');$title = "Редактирование статьи " . $title;$content = view_include('v_edit.php', array('ar_content' => $ar_content,         'ar_title' => $ar_title, 'id' => $id, 'site_theme' => $site_theme));echo view_include('v_main.php', array('content' => $content,         'title' => $title, 'id' => $id, 'site_theme' => $site_theme));?>