<?include_once('models/m_startup.php');include_once('models/m_article.php');$base = new Base();// запуск сессии.$base->startup();$art = new Articles();if (isset($_GET['id'])) {        $article = $art->articles_get($_GET['id']);    $ar_title = $article['title'];    $ar_content = $article['content'];    $id = $_GET['id'];    }if (isset($_POST['edit'])) {      $success = $art->articles_edit($_POST['id'], $_POST['title'], $_POST['content']);        $id = $_POST['id'];    $article = $art->articles_get($_POST['id']);    $ar_title = $article['title'];    $ar_content = $article['content'];}if (isset ($_POST['delete'])) {        $art->articles_delete($_POST['id']);    }$title = "Редактирование статьи " . $title;$content = $base->view_include('v_edit.php',         array(            'ar_content' => $ar_content,             'ar_title' => $ar_title,             'id' => $id            ));echo $base->view_include('v_main.php',         array(            'content' => $content,            'title' => $title,            'site_theme' => $base->site_theme,            'site_root_path' => $base->site_root_path            ));  ?>