<?phpinclude_once('config.php');include_once('models/startup.php');include_once('models/articles.php');// Установка параметров, подключение к БД, запуск сессии.startup();//Название страницы$title = 'Консоль редактора';// Извлечение статей.$articles = articles_all();// Вывод в шаблон.$content = view('editor', 		array('articles' => $articles));			echo view('main', 		array('content' 	=> 	$content, 			'title' 		=> 	$title, 			'site_name' 	=> 	SITE_NAME,			'site_tmp' 		=> 	SITE_TMP));