<?php//// Список всех статей//function articles_all(){	// Запрос.	$query = "SELECT * FROM ".DB_PREF."_articles ORDER BY id_article DESC";	$result = mysql_query($query);								if (!$result)		die(mysql_error());		// Извлечение из БД.	$n = mysql_num_rows($result);	$articles = array();	for ($i = 0; $i < $n; $i++)	{		$row = mysql_fetch_assoc($result);				$articles[] = $row;	}		return $articles;}//// Конкретная статья//function articles_get($id_article){	$id_article = (int)$id_article;		$query = "SELECT * FROM ".DB_PREF."_articles WHERE id_article = '$id_article'";	$result = mysql_query($query);		if (!$result)		die(mysql_error());		$article = mysql_fetch_assoc($result);			return $article;	}//// Добавить статью//function articles_new($title, $content){	// Подготовка.	$title = trim($title);	$content = trim($content);	// Проверка.	if ($title == '')		return false;		// Запрос.	$t = "INSERT INTO ".DB_PREF."_articles (title, content) VALUES ('%s', '%s')";		$query = sprintf($t, 	                 mysql_real_escape_string($title),	                 mysql_real_escape_string($content));		$result = mysql_query($query);								if (!$result)		die(mysql_error());			return true;}//// Изменить статью//function articles_edit($id_article, $title, $content){	// Подготовка.	$id_article = (int)$id_article;	$title = trim($title);	$content = trim($content);	// Проверка.	if ($title == '')		return false;		// Запрос.	$t = "UPDATE ".DB_PREF."_articles SET title = '$title', content = '$content'  WHERE id_article = '$id_article'";		$query = sprintf($t, 	                 mysql_real_escape_string($title),	                 mysql_real_escape_string($content));		$result = mysql_query($query);								if (!$result)		die(mysql_error());			return true;}//// Удалить статью//function articles_delete($id_article){	$id_article = (int)$id_article;	$query = "DELETE FROM ".DB_PREF."_articles WHERE id_article = '$id_article'";	$result = mysql_query($query);		if (!$result)		die(mysql_error());			return true;}//// Короткое описание статьи//function articles_intro($article){	if (strlen($article) > 400) {		$article = substr($article, 0, 400) . '...';	}	return $article;}