<?include_once('models/startup.php');include_once('models/article.php');// Установка параметров, подключение к БД, запуск сессии.startup();$articles = articles_all();$intros = articles_intro($articles);$title = "Главная";// Кодировка.header('Content-type: text/html; charset=windows-1251');// Вывод в шаблон.//include('theme/default/v_index.php');$content = view_include('v_index.php', array('articles' => $articles,    'intros' => $intros,    'site_theme' => $site_theme));echo view_include('v_main.php',         array(            'content' => $content,            'title' => $title,            'site_theme' => $site_theme,            'site_root_path' => $site_root_path            ));mysql_close();?>