<?php//$anonse = 90;//// Список всех статей//function articles_all() {    // Запрос.    $query = "SELECT * FROM pro_articles ORDER BY id_article DESC";    $result = mysql_query($query);    if (!$result)        die(mysql_error());    // Извлечение из БД.    $n = mysql_num_rows($result);    $articles = array();    for ($i = 0; $i < $n; $i++) {        $row = mysql_fetch_assoc($result);        $articles[] = $row;    }    return $articles;}//// Конкретная статья//function articles_get($id_article) {    // TODO    //     // Запрос.    $t = "SELECT * FROM pro_articles             WHERE id_article = '%d'";        $query = sprintf($t, $id_article);    $result = mysql_query($query);    if (!$result)        die(mysql_error());    // Извлечение из БД.        $article = array();    $row = mysql_fetch_assoc($result);    $article = $row;    return $article;}//// Добавить статью//function articles_new($title, $content) {    // Подготовка.    $title = trim($title);    $content = trim($content);    // Проверка.    if ($title == '')        return false;    // Запрос.    $t = "INSERT INTO pro_articles (title, content) VALUES ('%s', '%s')";    $query = sprintf($t, mysql_real_escape_string($title), mysql_real_escape_string($content));    $result = mysql_query($query);    if (!$result)        die(mysql_error());    return true;}//// Изменить статью//function articles_edit($id_article, $title, $content) {    // TODO        $sql = "UPDATE pro_articles SET title = '%s', content = '%s'         WHERE id_article = '%d'";        $query = sprintf($sql, mysql_real_escape_string($title),             mysql_real_escape_string($content), $id_article);         $result = mysql_query($query);    if (!$result)        die(mysql_error());    return true;    }//// Удалить статью//function articles_delete($id_article) {    // TODO        $id = mysql_real_escape_string($id_article);        $sql = "DELETE FROM pro_articles WHERE id_article = $id";        $result = mysql_query($sql);    if (!$result)        die(mysql_error());    header("Location: editor.php");    exit;}//// Короткое описание статьи//function articles_intro($article) {    // TODO    // $article - это ассоциативный массив, представляющий статью    $intros = array();        $anonse = 90;        foreach ($article as $intro) {    // Запрос.    $query = "SELECT LEFT(content, $anonse) as content, id_article FROM pro_articles             WHERE id_article = '" . $intro['id_article'] . "'                 ORDER BY id_article DESC";    $result = mysql_query($query);        if (!$result)        die(mysql_error());    // Извлечение из БД.            $row = mysql_fetch_assoc($result);    $row['content'] = $row['content'] . "...";    $intros[$row['id_article']] = $row['content'];        //$intros[] = $row;    }    return $intros;    }?>