<?phpinclude_once('models/startup.php');include_once('models/article.php');$base = new Base();// запуск сессии.$base->startup();$art = new Articles();// Обработка отправки формы.if (isset($_POST)){	if ($art->articles_new($_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}		$ar_title = $_POST['title'];	$ar_content = $_POST['content'];}else{	$ar_title = '';	$ar_content = '';}$title = "Добавить статью";$content = $base->view_include('v_new.php', array('ar_content' => $ar_content,         'ar_title' => $ar_title, 'site_theme' => $site_theme));echo $base->view_include('v_main.php',         array(            'content' => $content,            'title' => $title,            'site_theme' => $site_theme,            'site_root_path' => $site_root_path            ));?>