<?phpinclude_once('models/startup.php');include_once('models/article.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (isset($_POST)){	if (articles_new($_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}		$ar_title = $_POST['title'];	$ar_content = $_POST['content'];}else{	$ar_title = '';	$ar_content = '';}// Кодировка.header('Content-type: text/html; charset=windows-1251');// Вывод в шаблон.//include('theme/default/v_new.php');$title = "Добавить статью";$content = view_include('v_new.php', array('ar_content' => $ar_content,         'ar_title' => $ar_title, 'site_theme' => $site_theme));echo view_include('v_main.php',         array(            'content' => $content,            'title' => $title,            'site_theme' => $site_theme,            'site_root_path' => $site_root_path            ));mysql_close();?>